word = ""


class Solution:
    def exist(self, board: List[List[str]], word: str) -> bool:
        self.orie = [(0, 1), (1, 0), (-1, 0), (0, -1)]
        self.lenw = len(word)
        if not word :
            return False

        def check(x,y,cnt):
            print(x,y,board,cnt,word[cnt])
            if x<0 or x>=len(board) or y<0 or y>=len(board[0]) or board[x][y] != word[cnt]:
                return False
            return True

        def dfs(x,y, cnt):
            if check(x, y, cnt):
                if cnt == self.lenw-1:
                    return True
                board[x][y] = ''
                res = (dfs(x+self.orie[0][0],y+self.orie[0][1],cnt + 1) or dfs(x+self.orie[1][0],y+self.orie[1][1],cnt + 1)
                or dfs(x+self.orie[2][0],y+self.orie[2][1],cnt + 1) or dfs(x+self.orie[3][0],y+self.orie[3][1],cnt + 1))
                board[x][y] = word[cnt]
                return res
            else:
                return False

        for i in range(len(board)):
            for j in range(len(board[0])):
                if dfs(i, j, 0):
                    return True
        return False


if __name__ == '__main__':
    res = Solution()
    print(res.exist(board,word))
